Function New-User {Param ( $user )$Password   = 'pass@word1'$PasswordSS = ConvertTo-SecureString  -String $Password -AsPlainText -Force$NewUserHT  = @{AccountPassword       = $PasswordSS;                Enabled               = $true;                PasswordNeverExpires  = $true;                Description           = "$user - NU"                ChangePasswordAtLogon = $falsea                }#     Create one new user (me!) and add to enterprise and domain admins security groupsNew-ADUser @NewUserHT -SamAccountName $user `                      -UserPrincipalName "$user@contoso.com" `                      -Name "$user" `                      -DisplayName 'Cool Guy $user'$x = 1Add-ADPrincipalGroupMembership -Identity "CN=$user,CN=Users,DC=contoso,DC=com" `                               -MemberOf "CN=Enterprise Admins,CN=Users,DC=contoso,DC=com" ,                                          "CN=Domain Admins,CN=Users,DC=contoso,DC=com" }Set-alias nu New-User1..10 | foreach {nu "User$_"}